mapper.select:
SELECT COUNT(*)
FROM task
WHERE project_id = #{projectId}

WITH RECURSIVE dependency_tree AS ( SELECT td.task_id, td.depends_on_task_id, 1 AS depth FROM task_dependency td JOIN task t ON t.id = td.task_id WHERE t.project_id = #{projectId}
UNION ALL
SELECT dt.task_id, td.depends_on_task_id, dt.depth + 1 FROM dependency_tree dt JOIN task_dependency td ON dt.depends_on_task_id = td.task_id ) SELECT COALESCE(MAX(depth), 0) FROM dependency_tree

SELECT COUNT(*)
FROM project
WHERE team_id = #{teamId}

SELECT team_id
FROM project
WHERE id = #{projectId}
